app.use((req, res, next) => {
  if (!req.session.user) return res.status(403).send("Ikke logget ind");
  const roles = sitePermission(req.url);
  if (!roles.includes(req.session.user.role)) return res.status(403).send("Ingen adgang");
  next();
});

